export const geoApiOptions = {
  method: "GET",
  headers: {
    "X-RapidAPI-Key": "6122760704msh4e54b062159b9e9p17af7ajsn5661baaf6e9a",
    "X-RapidAPI-Host": "wft-geo-db.p.rapidapi.com",
  },
};

export const GEO_API_URL = "https://wft-geo-db.p.rapidapi.com/v1/geo";
export const WEATHER_API_URL = "https://api.openweathermap.org/data/2.5";
export const WEATHER_API_KEY = "5869267bdac8588577d72d465accd9e2";

export const getWeatherData = async (latitude, longitude) => {
  const apiKey = WEATHER_API_KEY;
  const apiUrl = `${WEATHER_API_URL}/onecall?lat=${latitude}&lon=${longitude}&exclude=minutely,hourly&appid=${apiKey}`;

  try {
    const response = await fetch(apiUrl);
    const data = await response.json();
    console.log(data);
    // Process the weather data here
  } catch (error) {
    console.error('Error fetching weather data:', error);
  }
};

// Example usage:
// Replace these values with the actual latitude and longitude of the location
const latitude = -30.5595;
const longitude = 22.9375;

// Call the function to get weather data
getWeatherData(latitude, longitude);
